import axios from "axios";
import { useEffect, useState } from "react";
import { Button, Card, Col, Container, ListGroup, Row } from "react-bootstrap";
import { useNavigate, useParams } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faAt,
  faPhone,
  faCalendarCheck,
  faCircleCheck,
  faBuildingUser,
} from "@fortawesome/free-solid-svg-icons";

function GovclienteDetails({ apiURL }) {
  const [cliente, setcliente] = useState({});
  const { idCliente } = useParams();
  const navigate = useNavigate();
  useEffect(() => {
    try {
      const fetchcliente = async () => {
        const response = await axios.get(
          `${apiURL}/listarCliente/${idCliente}`
        );
        setcliente(response.data);
      };

      fetchcliente();
    } catch (error) {
      console.log(error);
    }
  }, [idCliente]);

  return (
    <Container
      style={{ height: "100vh" }}
      className="d-flex justify-content-center align-items-center"
    >
      <Card className="text-center w-100">
        <Card.Header>
          <Card.Title className="m-0">
            <h3>{cliente.nome}</h3>
          </Card.Title>
        </Card.Header>
        <Card.Body>
          <Card.Title>Informações Funcionais</Card.Title>
          <p />
          <Row>
           
            <Col>
              <ListGroup variant="flush">
                <ListGroup.Item action variant="primary">
                  Matrícula: {cliente.idCliente}
                </ListGroup.Item>
                <ListGroup.Item action variant="secondary">
                  Sexo: {cliente.sexo}
                </ListGroup.Item>
               
              </ListGroup>
            </Col>
            <Card.Title>Informações de Endereço</Card.Title>
            <p />
            <Col>
              <ListGroup variant="flush">
                <ListGroup.Item action variant="primary">
                  Rua: {cliente.rua}
                </ListGroup.Item>
                <ListGroup.Item action variant="secondary">
                  Numero: {cliente.numero}
                </ListGroup.Item>
                <ListGroup.Item action variant="success">
                  Cidade: {cliente.cidade}
                </ListGroup.Item>
              </ListGroup>
            </Col>
          </Row>
          <Row>
            <Card.Title>Informações para Contato</Card.Title>
            <Col>
              <ListGroup variant="flush">
                <ListGroup.Item action variant="primary">
                  Email: {cliente.email}
                </ListGroup.Item>
                <ListGroup.Item action variant="secondary">
                  Celular: {cliente.telefone}
                </ListGroup.Item>
                <ListGroup.Item action variant="success">
                  Celular: {cliente.celular}
                </ListGroup.Item>
              </ListGroup>
            </Col>
            
          </Row>
          <Row className="mt-3">
            <Col>
              <Button variant="secondary" onClick={() => navigate(-1)}>
                Voltar
              </Button>
            </Col>
          </Row>
        </Card.Body>
      </Card>
    </Container>
  );
}

export default GovclienteDetails;
