import axios from "axios";
import { useEffect, useState } from "react";
import { Button, Container, Form, Spinner, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

function AnimalList({ apiURL }) {
    const [animals, setAnimals] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [search, setSearch] = useState("");
    const navigate = useNavigate();
    const path = '/listarAnimais';
    const url = `${apiURL}${path}`;

    useEffect(() => {
        const fetchAnimals = async () => {
            try {
                const response = await axios.get(url);
                setAnimals(response.data);
                setIsLoading(false);
            } catch (error) {
                console.log(error);
            }
        };

        fetchAnimals();
    }, [url]);

    const deleteAnimal = async (idAnimal) => {
        await axios.delete(`${apiURL}/excluirAnimal/${idAnimal}`);
        navigate("/listarAnimais");
    };

    const renderAnimals = animals
        .filter((animal) => animal.nome.toLowerCase().includes(search.toLowerCase()))
        .map((animal) => {
            return (
                <tr key={animal.idAnimal}>
                    <td>{animal.nome}</td>
                    <td>{animal.raca}</td>
                    <td>{animal.especie}</td>
                    <td>{animal.cor}</td>
                    <td>{animal.proprietario ? animal.proprietario.nome : 'Sem proprietÃ¡rio'}</td>
                    <td style={{width: '250px'}}>
                        <Link className="btn btn-outline-primary btn-sm m-1" role="button"
                              to={`/listarAnimal/${animal.idAnimal}`}>Detalhar</Link>
                        <Link className="btn btn-outline-secondary btn-sm m-1" role="button"
                              to={`/editarAnimal/${animal.idAnimal}`}>Alterar</Link>
                        <Button variant="danger" size="sm" onClick={() => {
                            window.confirm("Deseja realmente EXCLUIR?") && deleteAnimal(animal.idAnimal);
                        }}>Excluir</Button>
                    </td>
                </tr>
            );
        });

    return (
        <Container>
            <Form className="my-4">
                <Form.Group controlId="search">
                    <Form.Label>Pesquisar Animais</Form.Label>
                    <Form.Control
                        type="text"
                        placeholder="Digite o nome do animal"
                        value={search}
                        onChange={(e) => setSearch(e.target.value)}
                    />
                </Form.Group>
                <Button variant="info" size="sm">
                    <Link className="nav-link" to="/cadastrarAnimal">Novo Animal</Link>
                </Button>
            </Form>
            {isLoading && <Spinner className="" animation="border" />}
            {!isLoading &&
                <Table className="mt-4" striped bordered hover>
                    <thead>
                    <tr>
                        <th>Nome</th>
                        <th>Raca</th>
                        <th>EspÃ©cie</th>
                        <th>Cor</th>
                        <th>ProprietÃ¡rio</th>
                        <th>AÃ§Ãµes</th>
                    </tr>
                    </thead>
                    <tbody>
                    {renderAnimals}
                    </tbody>
                </Table>
            }
        </Container>
    );
}

export default AnimalList;
