import React, {useEffect ,useState, Select } from "react";
import { Container, Form, Button,Col } from "react-bootstrap";
import Image from 'react-bootstrap/Image';
import Logo from './images/vet.png';
import "./index.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";
function CadastroUsuario({ apiURL, form, setForm }) {
  const navigate = useNavigate();
  const path = "/cadastrarFuncionario";
  const url = `${apiURL}${path}`;
  
  const handleChange = (e) => {
    //monitoramento dos inputs
    setForm({ ...form, [e.target.name]: e.target.value });
  };
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(url, form);
      navigate("/listarFuncioarios");
    } catch (error) {
      if (error.response && error.response.status === 403) {
        // Usuário não tem permissão para acessar o recurso
        alert("Você não tem permissão para cadastrar Funcionarios.");
      } else {
        // Outro erro ocorreu
        console.error("Erro:", error.message);
      }
    }
  };


  useEffect(() => {
    setForm({
      cpf: "",
      email: "",
      senha:"",
      role:""
    });
  }, []);


  return (
  
    <div className="d-flex justify-content-center align-items-center vh-100 bg-dark p-5">
      <Col xs={6} md={4}>
          <Image src={Logo}  />
        </Col>
      <Container className="p-5 bg-success text-white border border-success-subtle" style={{ maxWidth: "500px", borderRadius: "10px" } }>
        <h1>Cadastro de Usuário</h1>
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="formNome">
            <Form.Label >Nome</Form.Label>
            <Form.Control
                required
                type="text"
                placeholder="Insira o nome completo do Proprietário"
                name="nome"
                value={form.nome}
                onChange={handleChange}
              />
          </Form.Group>

          <Form.Group controlId="formEmail">
            <Form.Label className="mb-3">Email</Form.Label>
            <Form.Control
              type="email"
              placeholder="Digite seu email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group controlId="formSenha">
            <Form.Label className="mb-3">Senha</Form.Label>
            <Form.Control
              type="password"
              placeholder="Digite sua senha"
              value={senha}
              onChange={(e) => setSenha(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group>
          <Form.Label className="mb-3">Cargo</Form.Label>
            <Form.Control
              type="text"
              placeholder="Admin,Veterinário,Funcionario..."
              value={role}
              onChange={(e) => setRole(e.target.value)}
              required
            />
          </Form.Group>


          <Button variant="light" type="submit" className="mb-0 m-3">
            Cadastrar
          </Button>
        </Form>
      </Container>
    </div>
  );
}

export default CadastroUsuario;
