import React, { useState } from "react";
import { Container, Form, Button } from "react-bootstrap";
import InputGroup from 'react-bootstrap/InputGroup';

function CadastroUsuario() {
  // Estados para armazenar os valores dos campos do formulário
  const [nome, setNome] = useState("");
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [confirmarSenha, setConfirmarSenha] = useState("");

  // Função para lidar com o envio do formulário
  const handleSubmit = (e) => {
    e.preventDefault();
    // Aqui você pode implementar a lógica para enviar os dados para o backend
    console.log("Dados do formulário:", { nome, email, senha });
    // Limpar os campos após o envio do formulário
    setNome("");
    setEmail("");
    setSenha("");
    setConfirmarSenha("");
  };

  return (
    <Container >
      <h1 >Cadastro de Usuário</h1>
      <Form onSubmit={handleSubmit} >
        <Form.Group controlId="formNome "className=" mb-2 bg-success-subtle" >
          <InputGroup className="mb-3">Nome</InputGroup>
          <Form.Control
            type="text"
            placeholder="Digite seu nome"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group controlId="formEmail">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            placeholder="Digite seu email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group controlId="formSenha">
          <Form.Label>Senha</Form.Label>
          <Form.Control
            type="password"
            placeholder="Digite sua senha"
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
            required
          />
        </Form.Group>

        <Form.Group controlId="formConfirmarSenha">
          <Form.Label>Confirmar Senha</Form.Label>
          <Form.Control
            type="password"
            placeholder="Confirme sua senha"
            value={confirmarSenha}
            onChange={(e) => setConfirmarSenha(e.target.value)}
            required
          />
        </Form.Group>

        <Button variant="success" type="submit">
          Cadastrar
        </Button>
      </Form>
    </Container>
  );
}

export default CadastroUsuario;
