import axios from "axios";
import { useEffect, useState } from "react";
import { Button, Container, Form, Spinner, Table } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";

function AnimalList({ apiURL }) {
    const [animals, setAnimals] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [search, setSearch] = useState("");
    const navigate = useNavigate();
    const path = '/listarAnimais';
    const url = `${apiURL}${path}`;

    useEffect(() => {
        const fetchAnimals = async () => {
            try {
                const response = await axios.get(url);
                // Verifica se a resposta é um array antes de definir o estado
                if (Array.isArray(response.data)) {
                    setAnimals(response.data);
                } else {
                    setAnimals([]); // Define um array vazio se a resposta não for um array
                }
                setIsLoading(false);
            } catch (error) {
                console.log(error);
                setIsLoading(false);
            }
        };

        fetchAnimals();
    }, [url]);

    const deleteAnimal = async (idAnimal) => {
        try {
            await axios.delete(`${apiURL}/excluirAnimal/${idAnimal}`);
            // Atualiza a lista de animais após a exclusão
            setAnimals(animals.filter(animal => animal.idAnimal !== idAnimal));
        } catch (error) {
            console.error("Erro ao excluir animal:", error);
        }
    };

    const renderAnimals = animals
        .filter((animal) => animal.nome.toLowerCase().includes(search.toLowerCase()))
        .map((animal) => (
            <tr key={animal.idAnimal}>
                <td>{animal.nome}</td>
                <td>{animal.raca}</td>
                <td>{animal.especie}</td>
                <td>{animal.cor}</td>
                <td>{animal.proprietario && animal.proprietario.nome}</td>
                <td style={{ width: '250px' }}>
                    <Link className="btn btn-outline-primary btn-sm m-1" role="button" to={`/listarAnimal/${animal.idAnimal}`}>Detalhar</Link>
                    <Link className="btn btn-outline-secondary btn-sm m-1" role="button" to={`/editarAnimal/${animal.idAnimal}`}>Alterar</Link>
                    <Button variant="danger" size="sm" onClick={() => {
                        if (window.confirm("Deseja realmente EXCLUIR?")) deleteAnimal(animal.idAnimal);
                    }}>Excluir</Button>
                </td>
            </tr>
        ));

    return (
        <Container>
            <Form className="my-4">
                <Form.Control
                    type="text"
                    placeholder="Buscar por nome"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />
                <Button variant="info" size="sm" className="mt-3">
                    <Link className="nav-link" to="/cadastrarAnimal" style={{ color: '#fff' }}>Novo Animal</Link>
                </Button>
            </Form>
            {isLoading ? <Spinner animation="border" /> : (
                <Table className="mt-4" striped bordered hover>
                    <thead>
                        <tr>
                            <th>Nome</th>
                            <th>Raça</th>
                            <th>Espécie</th>
                            <th>Cor</th>
                            <th>Proprietário</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {renderAnimals}
                    </tbody>
                </Table>
            )}
        </Container>
    );
}

export default AnimalList;
