import axios from "axios";
import { useEffect } from "react";
import { Button, Col, Container, Form, Row } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { IMaskInput } from "react-imask";

function AddAnimal({ apiURL, form, setForm }) {
  const navigate = useNavigate();
  const path = "/cadastrarAnimal";
  const url = `${apiURL}${path}`;

  const handleChange = (e) => {
    //monitoramento dos inputs
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(url, form);
      navigate("/listarAnimais");
    } catch (error) {
      if (error.response && error.response.status === 403) {
        // Usuário não tem permissão para acessar o recurso
        alert("Você não tem permissão para cadastrar clientes.");
      } else {
        // Outro erro ocorreu
        console.error("Erro:", error.message);
      }
    }
  };

  useEffect(() => {
    setForm({
      idAnimal: "",
      nome: "",
      sexo: "",
      idade: "",
      peso: "",
      raca: "",
      alergia: "",
      cor: "",
      especie: "",
      proprietario: "",
    });
  }, []);

  return (
    <Container>
      <p />
      <h2>Cadastrar Novo Animal</h2>
      <Form onSubmit={handleSubmit}>
        <Row>
          <Col>
            <Form.Group className="mb-3">
              <Form.Label>Nome do Animal</Form.Label>
              <Form.Control
                required
                type="text"
                placeholder="Insira o nome completo do Animal"
                name="nome"
                value={form.nome}
                onChange={handleChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group className="mb-5">
              <Form.Label>Número da matrícula</Form.Label>
              <Form.Control
                required
                type="number"
                placeholder="Insira o número da matrícula"
                name="idAnimal"
                value={form.idAnimal}
                onChange={handleChange}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <h4>Informações Funcionais</h4>
        </Row>
        <Row>
          <Col>
            <Form.Group className="mb-3">
              <Form.Label>Sexo</Form.Label>
              <Form.Control
                required
                type="text"
                placeholder="Insira o nome o sexo do Animal"
                name="sexo"
                value={form.sexo}
                onChange={handleChange}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <h4>Informações Adicionais</h4>
        </Row>
        <Row>
          <Col>
            <Form.Group className="mb-3">
              <Form.Label>Idade</Form.Label>
              <Form.Control
                required
                type="text"
                placeholder="Insira a Idade do Animal"
                name="idade"
                value={form.idade}
                onChange={handleChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group className="mb-3">
              <Form.Label>Peso</Form.Label>
              <Form.Control
                required
                type="text"
                placeholder="Insira o Peso do Animal"
                name="peso"
                value={form.peso}
                onChange={handleChange}
              />
            </Form.Group>
          </Col>
          
          <Col>
            <Form.Group className="mb-3">
              <Form.Label>Raça</Form.Label>
              <Form.Control
                required
                type="text"
                placeholder="Insira a Raça do Animal"
                name="raca"
                value={form.raca}
                onChange={handleChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group className="mb-3">
              <Form.Label>Alergia</Form.Label>
              <Form.Control
                required
                type="text"
                placeholder="O Animal possuí alergia?"
                name="alergia"
                value={form.alergia}
                onChange={handleChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group className="mb-3">
              <Form.Label>Cor</Form.Label>
              <Form.Control
                required
                type="text"
                placeholder="Insira a Cor do Animal"
                name="cor"
                value={form.cor}
                onChange={handleChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group className="mb-3">
              <Form.Label>Espécie</Form.Label>
              <Form.Control
                required
                type="text"
                placeholder="Insira a Espécie do Animal"
                name="especie"
                value={form.especie}
                onChange={handleChange}
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group className="mb-3">
              <Form.Label>Proprietário</Form.Label>
              <Form.Control
                required
                type="text"
                placeholder="Insira o id do Proprietario"
                name="proprietario"
                value={form.proprietario}
                onChange={handleChange}
              />
            </Form.Group>
          </Col>
        </Row>
       

        <p />
        <Button
          variant="secondary"
          onClick={() => navigate(-1)}
          style={{ margin: "10px" }}
        >
          Voltar
        </Button>
        <Button variant="success" type="submit">
          Cadastrar
        </Button>
        <p />
      </Form>
    </Container>
  );
}

export default AddAnimal;
