import { Button, Container, Row } from "react-bootstrap";
import { Link } from "react-router-dom";
import React, { useState } from 'react';
import { useForm } from 'react-hook-form';

function HomePage() {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');

  const onSubmit = (data) => {
    console.log(data);
    // Salvar dados em banco de dados, enviar para API, etc.
  };

  return (
    <form className="container mt-3">
      <div className="row">
        <div className="col-md-6">
          <div className="form-group">
            <label htmlFor="nome" className="form-label">Nome:</label>
            <input type="text" className="form-control" id="nome" {...register('nome', { required: true })} />
            {errors.nome && <span className="text-danger">Nome é obrigatório</span>}
          </div>
          <div className="form-group">
            <label htmlFor="email" className="form-label">Email:</label>
            <input type="email" className="form-control" id="email" {...register('email', { required: true, pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i })} />
            {errors.email && <span className="text-danger">Email inválido</span>}
          </div>
        </div>
        <div className="col-md-6">
          <div className="form-group">
            <label htmlFor="senha" className="form-label">Senha:</label>
            <input type="password" className="form-control" id="senha" {...register('senha', { required: true, minLength: 6 })} />
            {errors.senha && <span className="text-danger">Senha precisa de no mínimo 6 caracteres</span>}
          </div>
        </div>
      </div>
      <button type="submit" className="btn btn-primary mt-3">Cadastrar</button>
    </form>
  );
}

export default HomePage;
