import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import './CadastroUsuario.css'; // Import custom CSS file

function CadastroUsuario() {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');

  const onSubmit = (data) => {
    console.log(data); // Data submitted by the form
    // Implement logic to save data to database, send to API, etc.
  };

  return (
    <div className="container-form">
      <h2>Cadastro de Usuário</h2>
      <div className="container mt-3">
        <div className="row">
          <div className="col-md-4 offset-md-4">
            <div className="card card-primary border-0 shadow p-4">
              <form onSubmit={handleSubmit(onSubmit)}>
                <div className="form-group">
                  <label htmlFor="nome" className="text-md-start form-label">Nome:</label>
                  <input type="text" className="form-control" id="nome" {...register('nome', { required: true })} />
                  {errors.nome && <span className="text-danger">Nome é obrigatório</span>}
                </div>
                <div className="form-group">
                  <label htmlFor="email" className="text-md-start form-label">Email:</label>
                  <input type="email" className="form-control" id="email" {...register('email', { required: true, pattern: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i })} />
                  {errors.email && <span className="text-danger">Email inválido</span>}
                </div>
                <div className="form-group">
                  <label htmlFor="senha" className="text-md-start form-label">Senha:</label>
                  <input type="password" className="form-control" id="senha" {...register('senha', { required: true, minLength: 6 })} />
                  {errors.senha && <span className="text-danger">Senha precisa de no mínimo 6 caracteres</span>}
                </div>
                <button type="submit" className="btn btn-block btn-lg btn-outline-primary mt-3">Cadastrar</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

}
