import axios from "axios";
import { useEffect, useState } from "react";
import { Button, Container, Form, FormLabel, Spinner, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";

function AnimalList({ apiURL }) {
    const [animals, setAnimals] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [search, setSearch] = useState("");
    const navigate = useNavigate();
    const path = '/listarAnimais';
    const url = `${apiURL}${path}`;

    useEffect(() => {
        try {
            const fetchAnimals = async () => {
                const response = await axios.get(url)
                setAnimals(response.data)
                setIsLoading(false)
            }

            fetchAnimals()
        } catch (error) {
            console.log(error)
        }
    }, []);

    const deleteAnimal= async (idAnimal) => {
        await axios.delete(`${apiURL}/excluirAnimal/${idAnimal}`);
        navigate("/listarAnimais");
    };

    const renderAnimals = animals
        .filter((animal) => animal.nome.toLowerCase().includes(search.toLowerCase()))
        .map((animal) => {
            return (
                <tr key={animal._id}>

                    <td>{animal.nome}</td>
                    <td>{animal.raca}</td>
                    <td>{animal.especie}</td>
                    <td>{animal.cor}</td>
                    <td>{animal.prorietario}</td>
                    <td style={{width: '250px'}}>
                        <Link className="btn btn-outline-primary btn-sm m-1" role="button"
                              to={`/listarAnimal/${animal.idAnimal}`}>Detalhar</Link>
                        <Link className="btn btn-outline-secondary btn-sm m-1" role="button"
                              to={`/editarAnimal/${animal.idAdnimal}`}>Alterar</Link>
                        <Button variant="danger" size="sm" onClick={() => {
                            window.confirm("Deseja realmente EXCLUIR?") && deleteAnimal(animal.idAdnimal)
                        }}>Excluir</Button>
                    </td>
                </tr>
            )
        })

    return (
        <Container>
            <Form className="my-4">

                < p/>
                <Button variant="info" size="sm">
                    <Link className="nav-link" to="/cadastrarAnimal">Novo Animal</Link>
                </Button>
            </Form>
            {isLoading && <Spinner className="" animation="border" />}
            {!isLoading &&
                <Table className="mt-4" striped bordered hover>
                    <thead>
                    <tr>
                        <th>Nome</th>
                        <th>Raca</th>
                        <th>Esp√©cie</th>
                        <th>Cor</th>
                        <th>Proprietario</th>
                    </tr>
                    </thead>
                    <tbody>
                    { renderEmployees }
                    </tbody>
                </Table>
            }
        </Container>
    )
}

export default AnimalList;
