import React, { useState, useRef } from "react";
import axios from "axios";

function WebcamCapture({ onSnapshot, onClose }) {
  const [mediaStream, setMediaStream] = useState(null);
  const videoRef = useRef();
  const canvasRef = useRef();

  // Função para iniciar a captura da webcam
  const startWebcamCapture = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      setMediaStream(stream);
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }
    } catch (error) {
      console.error("Erro ao acessar a webcam:", error);
    }
  };

  // Função para tirar uma foto da webcam
  const takeSnapshot = () => {
    const video = videoRef.current;
    const canvas = canvasRef.current;
    if (video && canvas) {
      const context = canvas.getContext("2d");
      context.drawImage(video, 0, 0, canvas.width, canvas.height);
      const imageData = canvas.toDataURL("image/png");
      onSnapshot(imageData); // Envia a imagem capturada para o componente pai
    }
  };

  // Função para encerrar a captura da webcam
  const stopWebcamCapture = () => {
    if (mediaStream) {
      mediaStream.getTracks().forEach(track => track.stop());
      setMediaStream(null);
      onClose(); // Fecha o modal da webcam
    }
  };

  return (
    <div>
      <video ref={videoRef} autoPlay muted />
      <canvas ref={canvasRef} style={{ display: "none" }} />
      <button onClick={takeSnapshot}>Capturar Foto</button>
      <button onClick={stopWebcamCapture}>Encerrar Captura</button>
    </div>
  );
}

export default WebcamCapture;
